class CoordinateData {
  final List<int> xCoordinates;
  final List<int> yCoordinates;
  final List<int> zCoordinates;
  final String deviceId;
  final String timestamp;

  CoordinateData({
    required this.xCoordinates,
    required this.yCoordinates,
    required this.zCoordinates,
    required this.deviceId,
    required this.timestamp,
  });

  factory CoordinateData.fromJson(Map<String, dynamic> json) {
    List<List<int>> coordinates = List<List<int>>.from(
      json['coordinate']
          .map((coord) => List<int>.from(coord.map((x) => int.parse(x)))),
    );

    List<int> xCoordinates = coordinates.map((coord) => coord[0]).toList();
    List<int> yCoordinates = coordinates.map((coord) => coord[1]).toList();
    List<int> zCoordinates = coordinates.map((coord) => coord[2]).toList();

    DateTime dateTime = DateTime.fromMillisecondsSinceEpoch(
        int.parse(json['timestamp']) * 1000);

    return CoordinateData(
      xCoordinates: xCoordinates,
      yCoordinates: yCoordinates,
      zCoordinates: zCoordinates,
      deviceId: json['deviceId'],
      timestamp: DateFormat('yyyy-MM-dd HH:mm:ss').format(dateTime).toString(),
    );
  }
}

class DataTablePage extends StatelessWidget {
  final Map<String, dynamic> response;

  DataTablePage({required this.response});

  void _downloadCSV(BuildContext context, List<CoordinateData> dataList) async {
    List<List<dynamic>> dataAsListOfLists = [];

    for (var data in dataList) {
      for (var i = 0; i < data.xCoordinates.length; i++) {
        dataAsListOfLists.add([
          data.deviceId,
          DateFormat('yyyy-MM-dd HH:mm:ss')
              .format(DateTime.parse(data.timestamp)),
          data.xCoordinates[i],
          data.yCoordinates[i],
          data.zCoordinates[i],
        ]);
      }
    }

    List<String> columnNames = [
      'Device ID',
      'Timestamp',
      'X coorinate',
      'Y coordinate',
      'Z coordinate'
    ];
    dataAsListOfLists.insert(0, columnNames);

    String csv = const ListToCsvConverter().convert(dataAsListOfLists);
    if (!kIsWeb) {
      final directory = await getExternalStorageDirectory();

      if (directory != null) {
        final file = File('${directory.path}/data.csv');
        await file.writeAsString(csv);

        ScaffoldMessenger.of(context).showSnackBar(
          SnackBar(
            content: Text('CSV downloaded to ${file.path}'),
          ),
        );
      } else {
        ScaffoldMessenger.of(context).showSnackBar(
          SnackBar(
            content: Text('Error: Unable to access external storage.'),
          ),
        );
      }
    } else {
      final blob = Blob([csv], 'text/csv');
      final url = Url.createObjectUrlFromBlob(blob);

      AnchorElement(href: url)
        ..setAttribute("download", "data.csv")
        ..click();

      Url.revokeObjectUrl(url);

      ScaffoldMessenger.of(context).showSnackBar(
        SnackBar(
          content: Text('CSV download started.'),
        ),
      );
    }
  }

  @override
  Widget build(BuildContext context) {
    final List<dynamic> dataList = json.decode(response['body']);
    final List<CoordinateData> coordinateDataList =
    dataList.map((data) => CoordinateData.fromJson(data)).toList();

    return Scaffold(
      appBar: AppBar(
        title: Text('DataTable Page'),
      ),
      body: Center(
        child: Column(
