            SizedBox(height: 20),
            Expanded(
              child: SingleChildScrollView(
                scrollDirection: Axis.vertical,
                child: DataTable(
                  columns: [
                    DataColumn(
                      label: Text('Device ID'),
                      tooltip: 'Device ID',
                    ),
                    DataColumn(
                      label: Text('Timestamp'),
                      tooltip: 'Timestamp',
                    ),
                    DataColumn(
                      label: Text('X'),
                      tooltip: 'X',
                    ),
                    DataColumn(
                      label: Text('Y'),
                      tooltip: 'Y',
                    ),
                    DataColumn(
                      label: Text('Z'),
                      tooltip: 'Z',
                    ),
                  ],
                  rows: coordinateDataList
                      .map((data) => List.generate(
